import React, { useState } from "react";

const Home = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleModalToggle = () => {
    setIsModalOpen(!isModalOpen);
  };

  return (
    <div className="w-full h-screen home flex items-center px-5 lg:px-[72px]">
      <div className="max-w-[450px] md:max-w-[500px] lg:max-w-[600px] flex flex-col gap-7">
        <h1 className="text-[30px] md:text-[40px] lg:text-[50px] font-bold">
          AlerGlow: Free Skin Allergy Check
        </h1>
        <p className="text-text text-[14px] lg:text-[18px]">
          Alerglow is an innovative app that uses Convolutional Neural Network
          (CNN) algorithm to accurately detect skin allergies. With Alerglow,
          users can easily overcome the difficulty in identifying skin allergy
          symptoms that are often confusing. The app provides an effective and
          quick solution to know the type of skin allergy experienced, helping
          users in taking better care of their skin.
        </p>
        <div>
          <button
            className="py-2 px-8 bg-blue rounded-full"
            onClick={handleModalToggle}
          >
            Try Now
          </button>
        </div>
      </div>

      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-8 w-full max-w-md mx-4">
            <h2 className="text-2xl font-bold mb-4">Upload Photo</h2>
            <label className="block mb-4">
              <span className="sr-only">Choose File</span>
              <input
                type="file"
                className="block w-full text-sm text-gray-900 bg-gray-50 rounded-lg border border-gray-300 cursor-pointer focus:outline-none file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
              />
            </label>
            <button className="py-2 px-8 bg-blue-500 text-white rounded-full hover:bg-blue-600 transition-colors duration-300">
              Predict
            </button>
            <button
              className="py-2 px-8 bg-red-500 text-white rounded-full mt-4 hover:bg-red-600 transition-colors duration-300"
              onClick={handleModalToggle}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
